//     //   // controls.update();
//     //   renderer.render(scene,camera);
//     //   requestAnimationFrame(animate);
//     // }
//     // animate()
    
// })


animate()

function animate(){
  // controls.update();
  renderer.render(scene,camera);
  requestAnimationFrame(animate);
}
